import React, {useRef} from 'react'
import './searchbar.css'
import {Col, Form, FormGroup} from 'reactstrap'

import { BASE_URL } from '../utils/config'
import { useNavigate } from 'react-router-dom'

const SearchBar = () => {

    const locationRef = useRef('')
    const distanceRef = useRef(0)
    const maxGroupSizeRef = useRef(0)
    const navigate = useNavigate()

    const searchHandler = async() => {
        const location = locationRef.current.value;
        const distance = distanceRef.current.value;
        const maxGroupSize = maxGroupSizeRef.current.value;

        if(location === '' || distance === '' || maxGroupSize === ''){
            return alert('All fields are required!');
        }

        const res = await fetch
        (`${BASE_URL}/tours/search/getTourBySearch?city=${location}&distance=${distance}&maxGroupSize=${maxGroupSize}`)

        if(!res.ok) alert('Something went wrong');

        const result = await res.json()

        navigate(`/tours/search?city=${location}&distance=${distance}&maxGroupSize=${maxGroupSize}`, {state: result.data});

    };

  return <Col lg='12'>
  <div className='search__bar'>
    <Form className='d-flex align-items-center gap-4'> 

    <FormGroup className='d-flex gap-1 from__group from__group-fest'>
        <span> <i class="ri-map-pin-fill"></i> </span>
        <div>
            <h6>Location</h6>
            <input type='text' placeholder='where shall we go?' ref={locationRef} />
        </div>
    </FormGroup>

    <FormGroup className='d-flex gap-3 from__group from__group-fest'>
        <span> <i class="ri-map-pin-time-fill"></i> </span>
        <div>
            <h6>Distance</h6>
            <input type='number' placeholder='Distance km/m' ref={distanceRef} />
        </div>
    </FormGroup>

    <FormGroup className='d-flex gap-3 from__group from__group-last'>
        <span> <i class="ri-group-2-fill"></i> </span>
        <div>
            <h6>People</h6>
            <input type='number' placeholder='10' ref={maxGroupSizeRef}/>
        </div>
    </FormGroup>

    <span className="search__icon" type='submit' onClick={searchHandler}>
    <i class="ri-search-2-line"></i>
    </span>

    </Form>
  </div>
  </Col>
}

export default SearchBar
