import React,{useEffect, useRef, useState, useContext} from 'react'
import '../styles/tour-details.css'

import { Container, Row, Col, Form, ListGroup } from 'reactstrap'
import { useParams } from 'react-router-dom'
import calculateAvgRating from '../utils/avgRating'

import Booking from '../components/Booking/Booking'
import avatar from '../assets/images/avatar.jpg'

import Newsletter from '../shared/Newsletter'
import useFetch from './../hooks/useFetch'
import { BASE_URL} from './../utils/config'
import {AuthContext} from './../context/AuthContext'

const TourDetails = () => {

  const { id } = useParams();

  const rewiewMsgRef = useRef('');

  const [tourRating, setTourRating] = useState(null);

  const {user} = useContext(AuthContext)

  // featch data from database
  const {data:tour, loading, error} = useFetch(`${BASE_URL}/tours/${id}`)

  const { photo, title, desc, price, address, reviews, city,
    distance, maxGroupSize } = tour

  const { totalRating, avgRating } = calculateAvgRating(reviews)

  // format

  const options = {day: 'numeric', month: 'long', year: 'numeric' }

  // submit 

  const submitHandler = async e => {

    e.preventDefault()
    const reviewText = rewiewMsgRef.current.value;

    try {

      if(!user || user === undefined || user === null){
        alert('Please sign in ')
      }

      const reviewObj = {
        username:user?.username,
        reviewText,
        reting: tourRating,
      };

      const res = await fetch(`${BASE_URL}/review/${id}`,{
        method:'post',
        headers:{
          'content-type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(reviewObj)
      })

      const result = await res.json();
      if(!res.ok) {
        return alert(result.message);
      }
      alert(result.message)
      
    } catch (err) {
      alert(err.message);
    }

  };

  useEffect(() =>{
    window.scrollTo(0, 0)
  },[tour])

  return <>
    <section>
      <Container>
        {
          loading && <h4 className='text-center pt-5'>Loading...</h4>
        }
        {
          error && <h4 className='text-center pt-5'>{error}</h4>
        }
        {!loading && !error && <Row>
          <Col lg='8'>
            <div className="tour__content">
              <img src={photo} alt='photo' />
              <div className='tour__info'>

                <h2>{title}</h2>
                <div className='d-flex align-items-center gap-5'>
                  <span className='tour-rating d-flex align-items-center gap-2'>
                    <i class="ri-star-s-fill" style={{
                      color:
                        'var (--secondary-color)'
                    }}></i>
                    {avgRating === 0 ? null : avgRating}
                    {totalRating === 0 ? (
                      'Not rated'
                    ) : (
                      <span>({reviews?.length})</span>
                    )}
                  </span>

                  <span>
                    <i class="ri-road-map-fill"></i> {address}
                  </span>
                </div>

                <div className="tour__extra-details">
                  <span>
                    <i class="ri-map-pin-2-fill"></i> {city}
                  </span>
                  <span>
                    <i class="ri-copyright-fill"></i> {price}
                  </span>
                  <span>
                  <i class="ri-compass-3-line"></i> {distance} k/m
                  </span>
                  <span>
                    <i class="ri-group-line"></i> {maxGroupSize} people
                  </span>
                </div>
                <h5>Описание</h5>
                <p>{desc}</p>

              </div>

              {/* tour reviews section start */}

              <div className="tour__reviews mt-4">
                <h4>Reviews ({reviews?.length} reviews) </h4>

                <Form onSubmit={submitHandler}>
                  <div className='rating__group d-flex align-items-center gap-3 mb-4'>
                    <span onClick={() => setTourRating(1)}>
                      1 <i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => setTourRating(2)}>
                      2 <i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => setTourRating(3)}>
                      3 <i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => setTourRating(4)}>
                      4 <i class="ri-star-s-fill"></i>
                    </span>
                    <span onClick={() => setTourRating(5)}>
                      5 <i class="ri-star-s-fill"></i>
                    </span>
                  </div>

                  <div className="rewiew__input">
                    <input type='text' ref={rewiewMsgRef} 
                     placeholder='share your thoughts' required/>
                     
                    <button className='btn primary__btn text-white' type='submit'>
                      Submit
                    </button>
                  </div>
                </Form>

                <ListGroup className='user__reviews'> 
                {
                  reviews?.map(review =>(
                    <div className='rewiew__item'>
                      <img src={avatar} alt='avatar' />
                      <div className='w-100'>
                        <div className='d-flex align-items-center justify-content-between'>
                          
                          <div>
                            <h5>
                              {review.username}
                            </h5>
                            <p>
                              {new Date('11-11-2023').toLocaleDateString(
                                'en-US',
                                options
                                )}
                              </p>
                          </div>

                          <span className='d-flex align-items-center'>
                            {review.rating} <i class="ri-star-s-fill"></i>
                          </span>
                        </div>

                        <h6>{review.reviewText}</h6>

                      </div>
                    </div>
                  ))
                }
                </ListGroup>
              </div>
          
              {/* tour reviews section end */}

            </div>
          </Col>

          <Col ig='4'>
            <Booking tour={tour} avgRating ={avgRating}/>

          </Col>

        </Row>
        }

      </Container>
    </section>

    <Newsletter/>
  </>
}

export default TourDetails
